import heroBackground from '../images/hero-bg.png'; // Adjust the path as necessary

export function Hero() {
    const heroStyle = {
        backgroundImage: `url(${heroBackground})`,
        backgroundRepeat: 'no-repeat',
        // any other background properties you want to apply
    };
    return (
        <section className="hero">
            <div className="container">
                <div className="hero-content">
                    <h1 className="hero-heading text-xxl">A powerful solution to grow your startup. Fast!</h1>
                    <p className="hero-content">Organize, collaborate, and track progress seamlessly with our one-stop-shop
                        startup growth tool </p>
                    <div className="hero-buttons">
                        <a href="/getstarted">Get Started</a>
                        <a href="/bookdemo" className="btn"> <i class="fas fa-laptop" /> Book A Demo</a>
                    </div>
                </div>
            </div>
        </section>)
}
